FROM BOARD
LIMIT ${limit} OFFSET #{offset}

mapper.update:
update BOARD
set title=#{updateParam.title},
    AUTHOR=#{updateParam.author},
    CONTENTS=#{updateParam.contents}
where IDX = #{idx}

